/*!
 * Module dependenceis
 */

var apollo = require('../..');

/*!
 * Actor System
 */

var counter = apollo('counter');

/*!
 * Actors Prototypes
 */

var Listener = require('./listener')
  , Worker = require('./worker');

/*!
 * Actor Refs
 */

var listener = counter.actorOf('listener', Listener)
  , worker = counter.actorOf('worker', Worker);

/*!
 * Get on with it.
 */

worker
  .tell('start')
  .sender(listener);
