
exports.events = {
  'start': 'initialize'
};

exports.receive = {
    'increment': 'increment'
  , 'status': 'status'
};

exports.initialize = function (ev, done) {
  this.count = 0;
  done();
}

exports.increment = function (msg, done) {
  var val = msg.data.value;
  this.count += val;
  console.log(this.count);
  done();
};

exports.status = function (msg, done) {
  msg.respond(this.count);
  done();
};
