
var extend = require('tea-extend')
  , inherits = require('tea-inherits');

exports.augment = function (ctor, proto, klass) {
  var child = function () { return ctor.apply(this, arguments); };
  extend(child, ctor);
  inherits(child, ctor);
  if (proto) extend(child.prototype, proto);
  if (klass) extend(child, klass);
  return child;
};
